mapper.insert:

        SELECT ifnull(max(clnt_no),0) + 1 FROM tb_clnt

    INSERT INTO tb_clnt
	    (
	    clnt_no
	    ,clnt_name
	    ,clnt_tel
	    ,clnt_mng
	    ,clnt_hp
	    ,clnt_zip
	    ,bk_cd
	    ,clnt_acc
	    ,clnt_add
	    ,clnt_add_dt
	    ,clnt_indst
	    ,clnt_indst_no
	    ,clnt_memo
	    ,clnt_email
	    )
	   VALUES (
	    #{no} 
	    ,#{clnt_name}
	    ,#{clnt_tel} 
	    ,#{clnt_mng} 
	    ,#{clnt_hp} 
	    ,#{clnt_zip}
	    ,#{bkcombo}
	    ,#{clnt_acc}
	    ,#{clnt_add}
	    ,#{clnt_add_dt}
	    ,#{clnt_indst}
	    ,#{clnt_indst_no}
	    ,#{clnt_memo}
	    ,#{clnt_email}
	    )

mapper.update:
    UPDATE tb_clnt
    SET clnt_name = #{clnt_name}   
	    ,clnt_tel = #{clnt_tel}   
	    ,clnt_mng = #{clnt_mng}
	    ,clnt_hp = #{clnt_hp}
	    ,clnt_zip = #{clnt_zip}
	    ,bk_cd = #{bkcombo}
	    ,clnt_acc = #{clnt_acc}
	    ,clnt_add = #{clnt_add}
	    ,clnt_add_dt = #{clnt_add_dt}
	    ,clnt_indst = #{clnt_indst}
	    ,clnt_indst_no = #{clnt_indst_no}
	    ,clnt_memo = #{clnt_memo}
	    ,clnt_email = #{clnt_email}      
    WHERE clnt_no = #{clnt_no}
