mapper.insert:

        SELECT ifnull(max(splr_no),0) + 1 FROM tb_splr

    INSERT INTO tb_splr
	    (
	    splr_no
	    ,splr_name
	    ,splr_tel
	    ,splr_mng
	    ,splr_hp
	    ,splr_zip
	    ,bk_cd
	    ,splr_acc
	    ,splr_add
	    ,splr_add_dt
	    ,splr_indst
	    ,splr_indst_no
	    ,splr_memo
	    ,splr_email
	    )
	   VALUES (
	    #{no} 
	    ,#{splr_name}
	    ,#{splr_tel} 
	    ,#{splr_mng} 
	    ,#{splr_hp} 
	    ,#{splr_zip}
	    ,#{bkcombo}
	    ,#{splr_acc}
	    ,#{splr_add}
	    ,#{splr_add_dt}
	    ,#{splr_indst}
	    ,#{splr_indst_no}
	    ,#{splr_memo}
	    ,#{splr_email}
	    )

mapper.update:
    UPDATE tb_splr
    SET splr_name = #{splr_name}   
	    ,splr_tel = #{splr_tel}   
	    ,splr_mng = #{splr_mng}
	    ,splr_hp = #{splr_hp}
	    ,splr_zip = #{splr_zip}
	    ,bk_cd = #{bkcombo}
	    ,splr_acc = #{splr_acc}
	    ,splr_add = #{splr_add}
	    ,splr_add_dt = #{splr_add_dt}
	    ,splr_indst = #{splr_indst}
	    ,splr_indst_no = #{splr_indst_no}
	    ,splr_memo = #{splr_memo}
	    ,splr_email = #{splr_email}      
    WHERE splr_no = #{splr_no}
