mapper.select:
SELECT od.order_no ,DATE_FORMAT(od.order_date, '%Y-%m-%d') AS order_date ,cl.clnt_no ,cl.clnt_name ,cl.clnt_tel ,cl.clnt_email ,cl.clnt_zip ,cl.clnt_add ,cl.clnt_add_dt ,ui.name ,od.order_tot_price ,od.order_req FROM tb_order od INNER JOIN tb_clnt cl ON od.clnt_no = cl.clnt_no INNER JOIN tb_userinfo ui ON od.loginID = ui.loginID
and od.order_date >= STR_TO_DATE(#{start}, '%Y-%m-%d')
and od.order_date <= STR_TO_DATE(#{end}, '%Y-%m-%d')
and cl.clnt_no = #{clicombo}
ORDER BY od.order_no DESC LIMIT #{pageindex}, #{pageSize}

mapper.insert:

        SELECT ifnull(max(order_no),0) + 1 FROM tb_order

  	INSERT INTO tb_order 
	          (
	           order_no
	          ,clnt_no
	          ,order_tot_price
	          ,order_cancel
	          ,order_date
	          ,order_req
	          ,loginID
	          )
	  VALUES (
	           #{no}
	          ,#{clnt_no}
	          ,#{order_tot_price}
	          ,'N'
	          ,#{order_date}
	          ,#{order_req}
	          ,#{loginid}
	          );

INSERT INTO tb_order_dt 
	   	  (
		    order_no
		   ,product_no
		   ,clnt_no
		   ,product_price
		   ,order_dt_amt
		   ,order_dt_price
		   )
   VALUES (
   		     (SELECT ifnull(max(order_no),0) FROM tb_order)
   		   , #{product_no}
   		   , #{clnt_no}
   		   , #{product_price}
   		   , #{order_dt_amt}
   		   , #{order_dt_price}
   		   );
