mapper.select:
SELECT pl.pln_no ,pl.loginID ,pl.clnt_no ,(SELECT clnt_name FROM tb_clnt WHERE tb_clnt.clnt_no = pl.clnt_no) AS clnt_name ,pl.pln_date ,ll.product_no ,ll.pro_cd ,ll.pro_name ,ll.splr_no ,ll.splr_name ,ll.product_name ,pl.pln_amt ,pl.per_amt ,pl.pln_achv ,pl.pln_memo FROM ( SELECT pd.product_no ,pd.product_name ,pr.pro_cd ,pr.pro_name ,sp.splr_no ,sp.splr_name FROM tb_product pd INNER JOIN tb_pro pr ON pd.pro_cd = pr.pro_cd INNER JOIN tb_splr sp ON pd.splr_no = sp.splr_no ) ll INNER JOIN tb_planning pl ON pl.product_no = ll.product_no
and pl.loginID = #{currentLoginID}
and pl.loginID Like CONCAT('%', #{searchid}, '%')
and pl.clnt_no = #{searchclicombo}
and pl.pln_date = #{searchdate}
and ll.product_no = #{searchptypecombo}
ORDER BY pln_no DESC LIMIT #{pageindex}, #{pageSize}

SELECT COUNT(*) FROM ( SELECT pd.product_no ,pd.product_name ,pr.pro_cd ,pr.pro_name ,sp.splr_no ,sp.splr_name FROM tb_product pd INNER JOIN tb_pro pr ON pd.pro_cd = pr.pro_cd INNER JOIN tb_splr sp ON pd.splr_no = sp.splr_no ) ll INNER JOIN tb_planning pl ON pl.product_no = ll.product_no
and pl.loginID = #{currentLoginID}
and pl.loginID Like CONCAT('%', #{searchid}, '%')
and pl.clnt_no = #{searchclicombo}
and pl.pln_date = #{searchdate}
and ll.product_no = #{searchptypecombo}
